rule Bypass_Evasion_Payload
{
    meta:
        description = "Evasion bypass detection for specific memory operations"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 85 C0 0F 84 ?? ?? ?? ?? E8 ?? 8B 45 ?? EA EA DD }
        $pattern1 = { FF 75 ?? ?? ?? ?? ?? ?? ?? EB CA ?? }
        $pattern2 = { 6A ?? 5A 8B CE E8 ?? ?? ?? EA ?? ?? ?? }

    condition:
        any of them
}

