llm_query.yara_rule:
rule Bypass_Evasion
{
    meta:
        description = "Evasion bypass for conditional jumps in exit process"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 85 C0 0F 84 ?? ?? ?? ?? 8B 45 ?? }
        $pattern1 = { 6A ?? 5A 8B CE E8 ?? ?? ?? ?? 85 C0 }
        $pattern2 = { FF 75 08 E8C8FFFF ?? ?? ?? ?? 83 C4 85 C0 }

    condition:
        any of them
}

