llm_query.yara_rule:
rule Bypass_Sample
{
    meta:
        description = "Evasion bypass: Test EAX followed by conditional jump to skip exit call"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 6A ?? 5A ??: ?? ?? ?? ?? F2 1F ??: ?? ?? ?? }
        $pattern1 = { FD ?? FB ?? ?? ?? ?? F2 1F ?? ?? ?? ?? ?? }
        $pattern2 = { 8B ?? 45 ?? ?? ?? ?? F2 1F ?? ?? ?? ?? ?? }
    condition:
        any of them
}

