llm_query.yara_rule:
rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - specific to exit decision patterns"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 85C0 7412 6A5B 5A 8B4D FC }
        $pattern1 = { 3F3F E8E3 FAFFFF ??01 53 }
        $pattern2 = { 03C9 85C0 7407 50 E8FF FF00 }

    condition:
        any of them
}

