llm_query.yara_rule:
rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - specific instruction sequences"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 85C0 FF35FCC14100 }
        $pattern1 = { 8B 45 FC 7508 FF35FCC14100 }
        $pattern2 = { EB38 FF35FCC14100 }
    condition:
        any of them
}

cape_analysis.yara_rule:
rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - specific instruction sequences"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 85 C0 FF 35 FC C1 41 00 }
        $pattern1 = { 8B 45 FC 75 08 FF 35 FC C1 41 00 }
        $pattern2 = { EB 38 FF 35 FC C1 41 00 }
    condition:
        any of them
}

llm_query.yara_rule:
rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - evolved"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { [6-20 bytes with wildcards] }
        $pattern1 = { [6-20 bytes - DIFFERENT sequence] }
        $pattern2 = { [6-20 bytes - DIFFERENT sequence] }

    condition:
        any of them
}
