iterations.yara_rule:
rule EvasionBypass {
    meta:
        description = "Evasion bypass for unknown targets"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip"

    strings:
        $pattern0 = { 85 C0 0F 84 6A ?? 5A 8B CE E8 ?? EA AD DB CA DD 00 40 E7 C3 }
        $pattern1 = { FF C8 F1 FF FE FF ?? 5A 8B CE ?? CA DD 00 BF A9 DC }

    condition:
        any_of [
            matches.any($pattern0),
            matches.any($pattern1)
        ]
}

rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rule"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { E8 ?? ?? ?? ?? 8B }
        $pattern1 = { E9 ?? ?? ?? ?? C3 }
        $pattern2 = { FF ?? ?? 85 C0 }

    condition:
        any of them
}

