llm_query.yara_rule:
rule Bypass_Evasion
{
    meta:
        description = "Evasion bypass detection for multiple target calls"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 6A ?? 5A 8B CE E8 ?? ?? ?? ?? 85 C0 }
        $pattern1 = { E8 ?? ?? ?? ?? 74 ?? 0F 84 ?? ?? ?? ?? }
        $pattern2 = { 6D ?? CA ?? 9E 3C 6A ?? ?? ?? ?? }

    condition:
        any of them
}

