llm_query.yara_rule:
rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - specific test+jump+call patterns"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { [85C0], [0F85F2CFFFFF], [jne 00712403h], [push], [call dword ptr [esi+000000B0h]] }
        $pattern1 = { [85C0], [0F85F2CFFFFF], [jne 004053DEh], [xor], [mov], [call dword ptr [ebx+0Ch]] }
        $pattern2 = { [85C0], [0F85F2CFFFFF], [jne 0071240Bh], [push], [pop], [call dword ptr [esi+000000B0h]] }

    condition:
        (match $pattern0) || (match $pattern1) || (match $pattern2)
}

