        description = "Evasion bypass detection for specific memory access instructions"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { FF 15 88 A0 ?? ?? ?? ?? FA ???? }
        $pattern1 = { FF 15 88 A0 ?? ?? ?? ?? FC ???? }
        $pattern2 = { E8 C8 FF FF 75 08 push dword ptr [ebp+08h] }
    condition:
        any of them
}

