        description = "Evasion bypass rules for skipping detection"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = {
            6A ?? 5A 8B CE E8 C8 ??
        }
        $pattern1 = {
            FF7508 FF ?? ?? ?? ?? 8B 45
        }
        $pattern2 = {
            53 ?? 5B 8B EC F8 C8 ??
