llm_query.yara_rule:
rule Bypass_Sample_Evasion {
    meta:
        description = "Evasion bypass analysis for specific instruction sequences"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { [0x41E8BC, 0x52, 0x53, 0x41E8D0, 0x5E, 0x8B] }
        $pattern1 = { [0x41E937, 0x5E, 0x5D, 0x5F, 0x8B, 0x82] }
        $pattern2 = { [0x5A, 0x8D, 0x5E, 0x06, 0x8B, 0x5F] }

    condition:
        any of them
}

