llm_query.yara_rule:
rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rule targeting memory displacement calls"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,count=0"

    strings:
        $pattern0 = { 85 C0 0F 84 ?? ?? ?? ?? 8B 45 ?? }
        $pattern1 = { E8 25 05 00 00 00 00 00 } // Represents E8C50500 in hex
        $pattern2 = { 6A ?? 5A 8B CE E8 ?? ?? ?? ?? }
    condition:
        any of them
}

