llm_query.yara_rule:
rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rules targeting memory operand protection"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 
            8B EC FF75 08 E8 C8 FFFFFFFF
        }
        $pattern1 = { 
            8B EC FF75 08 4E 30 5A 
        }
        $pattern2 = { 
            8B 45 5A FF75 08 49 68 5A 
        }

    condition:
        any of them
}

