rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rule"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 85 C0 0F 84 ?? ?? ?? ?? 8B 45 ?? }
        $pattern1 = { E8 ?? ?? ?? ?? 83 C4 ?? 85 C0 0F 84 ?? ?? ?? ?? }
        $pattern2 = { FF 75 08 E8 C8 FF FF FF 8B CE E8 ?? ?? ?? ?? }

    condition:
        any of them
}

rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - evolved with specific exit process targets"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 8B 45 FC }
        $pattern1 = { FF 75 08 }
        $pattern2 = { FF 15 AC B0 41 00 }

    condition:
        (call_test_je AND E8C8FFFFFF) OR (push dword ptr [ebp+08h]) OR (FF15ACB04100)
}
