llm_query.yara_rule:
rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - evolved with multiple strategies"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 8B45FC 8BEC E8C8FFFFFF FF7508 push dword ptr [ebp+08h] 59 pop ecx 75 08 }
        $pattern1 = { E8C8FFFFFF FF7508 push dword ptr [ebp+08h] 59 pop ecx 75 08 FF15ACB04100 }
        $pattern2 = { 6A 5A 8B CE E8 C8FFFFFF FF7508 push dword ptr [ebp+08h] 59 pop ecx 75 08 }

    condition:
        any of the above patterns matched
}

