llm_query.yara_rule:
rule Bypass_Sample {
    meta:
        description = "Evasion bypass patterns for ExitProcess calls"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = {
            55 C9 FF7508 E8C8FFFFFF ???? ?? ?? ?? ???? FF EC ?? ?? 
            (PUSH ebp followed by a non-standard instruction and address)
        }
        $pattern1 = {
            8BFF FF7508 E8C8FFFFFF ???? ?? ?? ?? ???? FF EC ?? ?? 
            (MOV edi, esp followed by a non-standard instruction and address)
        }
        $pattern2 = {
            8BEC FF7508 ???? FF15F8104100 ???? FF EC ?? ?? 
            (MOBP ebp to esp with a different dword pointer and unknown addresses)
        }

    condition:
        any of them
}

