llm_query.yara_rule:
rule EvasionBypass {
    meta:
        description = "Evasion bypass patterns targeting execution exit"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { E8 ?? ?? ?? ?? 83 C4 ?? 85 C0 }
        $pattern1 = { 6A ?? 5A 8B CE E8 ?? ?? ?? ?? 85 C0 }
        $pattern2 = { 74/75 ?? ?? ?? ?? ?? }

    condition:
        any of them
}

