rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rules targeting various registers and function calls"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { [0x85C0, 0x7412, 0x5A, 0x8BCE, 0x03C9, 0x8BE5] }
        $pattern1 = { [0x6A5B, 0x5A, 0x8BEC, 0x03C3, 0x5B, 0x8B4D] }
        $pattern2 = { [0x03C9, 0x7412, 0x5E, 0x03C1, 0x5A, 0x8BE5] }

    condition:
        (any of the patterns match)
}

