llm_query.yara_rule:
rule Bypass_Sample
{
    meta:
        description = "Evasion bypass patterns for function calls using register pairs"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = {
            85 C0 05 F0 ?? ?? ?? ?? E8 45 ?? 
        }
        $pattern1 = {
            74 5C 00 0F 3D ?? ?? ??:8B ?? ?? ?? ?? 85 C0
        }
        $pattern2 = {
            6A 00 5A 8B CE E8 ?? ?? ?? ?? 85 C0 
        }

    condition:
        any of them
}

