llm_query.yara_rule:
rule Bypass_Sample
{
    meta:
        description = "Evasion bypass analysis"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 8B45 ?? ??.8B45F331CDh }
        $pattern1 = { E8 ?? ?? ?? ?? 83C4 ??.8B45508Ch }
        $pattern2 = { FF75 ?? 85C0 ?? FF158C50F400 }

    condition:
        any of them
}

