        description = "Evasion bypass rule targeting E8, 0F/84, and 6A sequences"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { E8 ?? 5A 6A 40 FC F3 }
        $pattern1 = { 0F 84 ED C9 7E 8B ?? CE ?? }
        $pattern2 = { 6A ?? 5A 8B CE E8 ??.?? }

    condition:
        any of them
}

