rule Bypass_StackOverflow
{
    meta:
        description = "Evasion bypass via stack overflow."
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = {
            55               ; Push ebp
            8BEC             ; Move ebp to esp
            FF7508           ; Push dword ptr [ebp+08h]
            FF155CC24C00     ; Call dword ptr [004CC25Ch]
            FFD2             ; FFD2 returns here
        }

        $pattern1 = {
            56               ; Push esi
            8B355C045100    ; Move esi to eax
            FF3510935200     ; Push dword ptr [00529310h]
            FF159CC24C00    ; Call dword ptr [004CC29Ch]
        }

        $pattern2 = {
            8B4D08           ; Move eax to dword ptr [edx+4Ch]
            8BE5             ; FFD2 returns here
        }

    condition:
        any_of_them
}

