llm_query.yara_rule:
rule Bypass_Evasion_Patterns {
    meta:
        description = "Evasion bypass detection using specific instruction sequences"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = {
            85 C0          # TEST EAX
            0F 84 33 FDFFFF # JZ with offset 0x20 (32)
            E8 CE           # CALL function
        }
        $pattern1 = {
            85 C0          # TEST EAX
            74 85 F0       # JE with offset 0x4C (60)
            E8 CE           # CALL function
        }
        $pattern2 = {
            85 C0          # TEST EAX
            5A             # POP EBX
            8B EC           # MOV ECX, ESI
            0F 84 33 FDFFFF # JZ with offset 0x20 (32)
            E8 CE           # CALL function
        }

    condition:
        any_of them
}

