rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rule targeting memory access and conditional jumps"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 85 C0 0F 84 FF FF FF ?? 8B 45 FF }
        $pattern1 = { E8 FF FF FF FF 83 C4 85 C0 0F 84 FF FF }
        $pattern2 = { 6A ?? 5A 8B CE E8 FF FF FF ?? 85 C0 }

    condition:
        any of them
}

