rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rule targeting specific exit paths"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = {85 C0 74 12 0F 84}
        $pattern1 = {6A C2 FF 75 08 E8 C8 FFFFFFFF}
        $pattern2 = {E8 CE 74 0C 33 CD}

    condition:
        any of them
}

