            85C0 0F84 33FF TEST EAX, JZ ??, POP ??, 7407 JE ??, POP ??, 5A,
            POP ??, 8BCE mov ecx, esi
        }
        $pattern1 = {
            6A5B PUSH 0x0B, POP ??, 8BCE mov ecx, esi, E825050000 call dword ptr [??],
            33CD xor ecx, ebp, 7407 JE ??, POP ??, 5A,
            POP ??, 8BCE mov ecx, esi
        }
        $pattern2 = {
            BA21050000 mov edx, 0x04h, 6A40 push 0x00000040h,
            E8E3FAFFFF call dword ptr [??], 7407 JE ??, POP ??,
            5A, POP ??, 8BCE mov ecx, esi
        }

    condition:
        any of them
}

