llm_query.yara_rule:
rule Bypass_StackManipulation
{
    meta:
        description = "Bypass Stack Manipulation"
        cape_options = "bp0=$pattern0+0,action0=skip"

    strings:
        $pattern0 = { 6A 40 53 68 40 11 E8 }

    condition:
        any of them
}

