llm_query.yara_rule:
rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rule"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 5E ?? 8B 45 FC FF D0 }
        $pattern1 = { 8B 45 FC FF D0 5E }
        $pattern2 = { FF D0 5E 8B 45 FC }

    condition:
        any of them
}

