llm_query.yara_rule:
rule Bypass_Sample {
    meta:
        description = "Evasion bypass rule"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { [6-20 bytes with wildcards] 85 C0 6A ?? 5B 5A 8B CE E82505?? ?? ?? ??? }
        $pattern1 = { [6-20 bytes with wildcards] E82505?? ?? ?? ?? 8BCE E874FAFFFF CALL 012F5C17h }
        $pattern2 = { [6-20 bytes with wildcards] 8D43?? ?? ?? ?? LEA EAX, [EBX+01] 33CD XOR ECX, EBP 5B POP EBX }

    condition:
        any of them
}

rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - evolved"
        cape_options = "bp0=$pattern0+10,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+5,action2=skip,count=0"

    strings:
        $pattern0 = { 85 C0 0F 84 ?? ?? ?? ?? 8B 45 FC }
        $pattern1 = { 83 F8 01 74 12 8B 4D F8 }
        $pattern2 = { 3D 00 10 00 00 0F 82 }

    condition:
        any of them
}

