llm_query.yara_rule:
rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rule"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 85 C0 74 12 6A 5B 5A 8B CE E825 05 00 00 call }
        $pattern1 = { FF 15 AC B0 41 00 push dword ptr [0041B0ACh] }
        $pattern2 = { FF 15 8C C4 68 00 push dword ptr [0086C42h] }

    condition:
        any of them
}

