rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rule"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { [6-20 bytes] 64 ?? A1 ?? ?? ?? 8B 40 ?? C1 E8 }
        $pattern1 = { [6-20 bytes] C1 E8 A8 ?? 01 75 ?? }
        $pattern2 = { [6-20 bytes] FF 15 ?? ?? ?? 20 50 FF 15 ?? ?? ?40 }

    condition:
        any of them
}

