llm_query.yara_rule:
rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rule"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 85 c0 ?? ff 74 24 04 ff d0 }
        $pattern1 = { ff d0 ff 74 24 04 }
        $pattern2 = { ff d0 FF 74 24 04 85 c0 }

    condition:
        any of them
}

