llm_query.yara_rule:
rule Bypass_Sample
{
    meta:
        description = "Evasion bypass rule"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 55 8BEC 53 56 57 6800404000 E890F6FFFF call 004015A0h }
        $pattern1 = { 83C408 add esp, 08h 85C0 test eax, eax 8B4508 mov eax, dword ptr [ebp+08h] }
        $pattern2 = { 6A00 push 00000000h E86BF3FFFF call 004012C0h 53 push ebx }

    condition:
        any of them
}

rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - evolved"
        cape_options = "bp0=$pattern0+0,action0=skip,count=0"

    strings:
        $pattern0 = { 85 C0 0F 84 ?? ?? ?? ?? 8B 45 FC }

    condition:
        any of them
}
