llm_query.yara_rule:
rule Pattern1
{
    meta:
        description = "Detects push ebx followed by call to ExitProcess"
    strings:
        $a = {53 FF15 ?? ?? ?? ??}
    condition:
        $a
}

rule Pattern2
{
    meta:
        description = "Detects test eax, eax followed by conditional jump to ExitProcess"
    strings:
        $b = {85 C0 74 07}
    condition:
        $b
}

rule Pattern3
{
    meta:
        description = "Detects full memory access, test, and conditional jump to ExitProcess"
    strings:
        $c = {A1 88 85 E7 00 85 C0 74 07}
    condition:
        $c
}

cape_analysis.yara_rule:
rule Pattern1
{
    meta:
        description = "Detects push ebx followed by call to ExitProcess"
        cape_options = "bp0=$a+0,action0=skip,count=0"
    strings:
        $a = { 53 FF 15 ?? ?? ?? ?? }
    condition:
        $a
}

final_yara_rule:
rule Pattern1
{
    meta:
        description = "Detects push ebx followed by call to ExitProcess"
        cape_options = "bp0=$a+0,action0=skip,count=0"
    strings:
        $a = { 53 FF 15 ?? ?? ?? ?? }
    condition:
        $a
}
