llm_query.yara_rule:
rule Pattern1
{
    meta:
        description = "First conditional jump and surrounding instructions"
    strings:
        $a = { 85 C0 74 12 6A ?? 5A 8B CE E8 ?? ?? ?? ?? }
    condition:
        $a
}

cape_analysis.yara_rule:
rule Pattern1
{
    meta:
        description = "First conditional jump and surrounding instructions"
        cape_options = "bp0=$a+0,action0=skip,count=0"
    strings:
        $a = { 85 C0 74 12 6A ?? 5A 8B CE E8 ?? ?? ?? ?? }
    condition:
        $a
}

