llm_query.yara_rule:
rule ConditionalExit1
{
    meta:
        description = "Conditional exit via test and jump followed by LEA"
    strings:
        $a = {85 C0 74 ?? 8D 45 FC}
    condition:
        $a
}

cape_analysis.yara_rule:
rule ConditionalExit1
{
    meta:
        description = "Conditional exit via test and jump followed by LEA"
        cape_options = "bp0=$a+0,action0=skip,count=0"
    strings:
        $a = { 85 C0 74 ?? 8D 45 FC }
    condition:
        $a
}

