llm_query.yara_rule:
rule ExitProcess_Call
{
    meta:
        description = "Detects a call to ExitProcess via a sequence of instructions."
        author = "Your Name"
    strings:
        $a = { E8 ?? ?? ?? ?? 53 E8 ?? ?? ?? ?? }
    condition:
        all_of(
            $a
        )
}

