    meta:
        description = "Detects evasion via call to ExitProcess"
    strings:
        $a = {55 8B EC FF 75 08 E8 ?? ?? ?? ??}
        $b = {59 FF 75 08 FF 15 ?? ?? ?? ??}
        $c = {FF 75 08 E8 ?? ?? ?? ??}
    condition:
        any of them
}

rule ExitProcess_Call1
{
