        description = "Detects a call to ExitProcess via a direct jump"
    strings:
        $a = { FF 75 08 E8 ?? ?? ?? ?? 59 }
    condition:
        $a
}

rule ExitProcess_Call2
{
    meta:
        description = "Detects a call to ExitProcess followed by a pop"
    strings:
        $a = { E8 ?? ?? ?? ?? 59 }
    condition:
        $a
}

rule ExitProcess_Call3
{
    meta:
        description = "Detects a call to ExitProcess via a memory address"
    strings:
