llm_query.yara_rule:
rule ExitProcess_Call1
{
    meta:
        description = "Detects a call to ExitProcess via a direct jump"
    strings:
        $a = { FF 75 08 E8 ?? ?? ?? ?? 59 }
    condition:
        $a
}

rule ExitProcess_Call2
{
    meta:
        description = "Detects a call to ExitProcess followed by a pop"
    strings:
        $a = { E8 ?? ?? ?? ?? 59 }
    condition:
        $a
}

rule ExitProcess_Call3
{
    meta:
        description = "Detects a call to ExitProcess via a memory address"
    strings:
        $a = { FF 75 08 FF 15 ?? ?? ?? ?? }
    condition:
        $a
}

cape_analysis.yara_rule:
rule ExitProcess_Call1
{
    meta:
        description = "Detects a call to ExitProcess via a direct jump"
        cape_options = "bp0=$a+0,action0=skip,count=0"
    strings:
        $a = { FF 75 08 E8 ?? ?? ?? ?? 59 }
    condition:
        $a
}
