llm_query.yara_rule:
rule Evasion_ExitProcess
{
    meta:
        description = "Detects evasion techniques involving the ExitProcess call"
        author = "YourName"
        date = "2023-10-05"

    strings:
        // Pattern 0: Call to ExitProcess preceded by push ebx
        $call_exitproc_0 = { 53 FF 15 ?? ?? ?? ?? } // 53 (push ebx) followed by call instruction

        // Pattern 1: Call to a function followed by memory read
        $call_func_1 = { E8 ?? ?? ?? ?? 8B 00 } // Call instruction followed by mov eax, [address]

        // Pattern 2: Test eax, eax followed by conditional jump
        $test_jump_2 = { 85 C0 0F 84 ?? ?? ?? ?? } // test eax, eax followed by je instruction

    condition:
        all of ($call_exitproc_0, $call_func_1, $test_jump_2)
}
