llm_query.yara_rule:
rule ExitPattern1
{
    meta:
        description = "Pattern 1: Call to ExitProcess with preceding push ebx"
    strings:
        $a = {53 E8 ?? ?? ?? ??}
    condition:
        all of them
}

rule ExitPattern2
{
    meta:
        description = "Pattern 2: Test eax, eax followed by je"
    strings:
        $b = {85 C0 74 ?? ?? ?? ??}
    condition:
        all of them
}

rule ExitPattern3
{
    meta:
        description = "Pattern 3: Test eax, eax followed by je (different displacement)"
    strings:
        $c = {85 C0 74 ?? ?? ?? ??}
    condition:
        all of them
}

cape_analysis.yara_rule:
rule ExitPattern1
{
    meta:
        description = "Pattern 1: Call to ExitProcess with preceding push ebx"
        cape_options = "bp0=$a+0,action0=skip,count=0"
    strings:
        $a = { 53 E8 ?? ?? ?? ?? }
    condition:
        all of them
}
