llm_query.yara_rule:
rule Malicious_Control_Flow_Or_ExitProcess_Call
{
    meta:
        description = "Detects potential malicious control flow or ExitProcess call patterns"
        author = "Your Name"
        date = "2025-04-05"

    strings:
        // Pattern 1: Test and Jump (likely to be used in control flow hijacking)
        $pattern1 = { 85 C0 74 12 6A 5B }

        // Pattern 2: Call to ExitProcess with subsequent instruction
        $pattern2 = { FF15 ?? ?? ?? ?? 8B 0D ?? ?? ?? ?? }

        // Pattern 3: Test and Jump with 32-bit offset (likely to be used in control flow hijacking)
        $pattern4 = { 85 C0 0F 84 ?? ?? ?? ?? }

    condition:
        any of ($pattern1, $pattern2, $pattern4)
}

