queries.yara_rule:
rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - evolved"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 85 C0 0F 85 ?? ?? ?? ?? }
        $pattern1 = { 83 7D E8 14 0F 82 ?? ?? ?? ?? }
        $pattern2 = { FF 96 B0 00 00 00 }

    condition:
        any of them
}

rule ConditionalCallPattern1
{
    meta:
        description = "Pattern capturing the jump to push instruction and call"
    strings:
        $a = { 0F 85 ?? ?? ?? ?? 6A 00 }
    condition:
        all of them
}

rule ConditionalCallPattern2
{
    meta:
        description = "Pattern capturing the test and conditional jump"
    strings:
        $a = { 85 C0 0F 85 ?? ?? ?? ?? }
    condition:
        all of them
}

rule ConditionalCallPattern3
{
