llm_query.yara_rule:
rule EvasionTechnique1
{
    meta:
        description = "Detects a suspicious test instruction with hardcoded immediate and subsequent DEC EAX"
    strings:
        $a9_48 = { A9 ?? ?? ?? ?? 48 }
    condition:
        $a9_48
}

rule EvasionTechnique2
{
    meta:
        description = "Detects repeated PUSH EBP followed by MOV EBP, ESP and stack offset access"
    strings:
        $55_45 = { 55 55 8B EC 8B 45 ?? }
    condition:
        $55_45
}

rule EvasionTechnique3
{
    meta:
        description = "Detects a CALL instruction followed by a TEST EAX, EAX operation"
    strings:
        $e8_85 = { E8 ?? ?? ?? ?? 85 C0 }
    condition:
        $e8_85
}

cape_analysis.yara_rule:
rule EvasionTechnique1
{
    meta:
        description = "Detects a suspicious test instruction with hardcoded immediate and subsequent DEC EAX"
        cape_options = "bp0=$a9_48+0,action0=skip,count=0"
    strings:
        $a9_48 = { A9 ?? ?? ?? ?? 48 }
    condition:
        $a9_48
}
