queries.yara_rule:
rule EvasionPatterns
{
    meta:
        description = "Detects potential evasion techniques in memory"
    strings:
        // Pattern 1: Call to ExitProcess with surrounding instructions
        $call_exitproc_1 = { 83 C4 14 52 FFD0 }
        // Pattern 2: sahf followed by jo and into instructions
        $sahf_jo_into = { 9E 70 ?? CE }
        // Pattern 3: Push sequence leading to a call
        $push_seq_call = { 51 56 50 E8 ?? ?? ?? ?? }
    condition:
        any of ($call_exitproc_1, $sahf_jo_into, $push_seq_call)
}

rule ExitCallSetup
{
    meta:
        description = "Identifies setup for indirect call to ExitProcess"
    strings:
        $a = { 83 C4 14 52 FFD0 }
    condition:
        $a
}

rule ExitPoint_Detector
{
    meta:
        description = "Detects the ExitProcess call and related control flow instructions"
        author = "Your Name"
        date = "2025-03-25"

    strings:
        $call_exit = { 8B 55 ?? 8B 06 FFD0 }
        $call_indirect = { 50 E8 ?? ?? ?? ?? }
        $call_indirect_next = { E8 ?? ?? ?? ?? 8B 55 ?? }

    condition:
        any of them
}
