llm_query.yara_rule:
rule ExitPointDetection
{
    meta:
        description = "Detects control flow instructions that lead to an early exit point in the code."
        author = "Your Name"
        date = "2025-03-25"

    strings:
        $call_to_exit = { E8 ?? ?? ?? ?? }
        $je_condition = { 0F 84 ?? ?? ?? ?? }
        $call_to_next = { E8 ?? ?? ?? ?? }

    condition:
        any of them
}

