llm_query.yara_rule:
rule FunctionCallPattern
{
    meta:
        description = "Detects function call patterns in code"

    strings:
        $a = { FF7508 FF15 ?? ?? ?? ?? }
        $b = { FF7508 E8 ?? ?? ?? ?? }
        $c = { E8 ?? ?? ?? ?? 59 }

    condition:
        any of them
}

cape_analysis.yara_rule:
rule FunctionCallPattern
{
    meta:
        description = "Detects function call patterns in code"
        cape_options = "bp0=$a+0,action0=skip,bp1=$b+0,action1=skip,bp2=$c+0,action2=skip,count=0"

    strings:
        $a = { FF 75 08 FF 15 ?? ?? ?? ?? }
        $b = { FF 75 08 E8 ?? ?? ?? ?? }
        $c = { E8 ?? ?? ?? ?? 59 }

    condition:
        any of them
}

llm_query.yara_rule:
rule FunctionPrologueSetup
{
    meta:
        description = "Standard function prologue setup"
    strings:
        $setup = { 55 8BEC FF7508 }
    condition:
        $setup
}

cape_analysis.yara_rule:
rule FunctionPrologueSetup
{
    meta:
        description = "Standard function prologue setup"
        cape_options = "bp0=$setup+0,action0=skip,count=0"
    strings:
        $setup = { 55 8B EC FF 75 08 }
    condition:
        $setup
}
