    strings:
        $pattern0 = { FFD2 5E 5F 5D C3 }
        $pattern1 = { 52 8B16 50 51 FFD2 58 C3 }
        $pattern2 = { 56 6A35 6A00 51 8DB0980C0000 56 50 E8??0A0000 }

