{
    meta:
        description = "Identifies function prologue and calls to ExitProcess/ExitThread"
    strings:
        $a = { FF 75 08 FF 15 ?? ?? ?? ?? }
        $b = { FF 75 08 E8 C8 ?? ?? ?? }
        $c = { 55 8B EC FF 75 08 }
    condition:
        any of them
}

